const ResetPassword = (a, b, c) => { axios.put(`/forgetPasswordReset/${a}/${c}`, { password: b }).then(() => displayPasswordReset()).catch(a => console.error(a)) }, displayPasswordReset = () => {
  $("#alerts").html(`
        <div className="uk-alert-success" uk-alert="true">
         
          <p className="uk-text-bold">Your password has been reset! You may login now!</p>
        </div>`), $("#password").val(""), $("#passwordConfirm").val(""), setTimeout(() => { window.location.href = "/login" }, 2e3)
}; $("#ResetUserPassword").on("click", a => { a.preventDefault(), $("#password").val() == $("#passwordConfirm").val() ? ResetPassword($("#uid").val(), $("#password").val(), $("#tid").val()) : $("#displayForgetPasswordReset").html("The password and confirmation password you entered in do not match.") });